function sendRequest( RequestLink , QData, onCompleteHandle)
{
    var URL = '/mb/mb_includes/jQuery.php';
    if( RequestLink!='')
        URL = RequestLink;    
        
    var myRequest = new Request({
        method: 'get',
        url: URL,
        onSuccess : onCompleteHandle
        }).send(QData);  
}

function setupProvCitySelector( proEle , cityEle , cityIndex)
{

    sendRequest( '','c=load_citynames_json_by_provinceid&p1='+proEle.value, function(res){          
            
           _j = JSON.decode( res );
           rebuildCityEle( cityEle , _j , cityIndex );           
            
        });    
    
    proEle.addEvent( 'change', function(){            
        sendRequest( '','c=load_citynames_json_by_provinceid&p1='+proEle.value, function(res){                      
           _j = JSON.decode( res );
           rebuildCityEle( cityEle , _j , -1);    
        });    
    });
}


function rebuildCityEle( cityEle , jsonObj , cityIndex )
{
    // remove all
    cityEle.options.length=0;     
    // add
    var i;
    for( i=0;i<jsonObj.length;i++)
    {
        var elOptNew = document.createElement('option');
        elOptNew.text = jsonObj[i].name;
        elOptNew.value = jsonObj[i].id
        if( cityIndex == jsonObj[i].id )
            elOptNew.selected = true;
        cityEle.options.add( elOptNew  );
    }    
}  